<?phpClass TOROFLIX_Sidebar {	public function create_sidebar_principal() {		register_sidebar( array(	        'name'          => __( 'Sidebar Principal', 'toroflix' ),	        'id'            => 'sidebar-principal',	        'description'   => __( 'Sidebar principal del theme', 'toroflix' ),	        'before_widget' => '<div id="%1$s" class="Wdgt %2$s">',	        'after_widget'  => '</div>',	        'before_title'  => '<div class="Title">',	        'after_title'   => '</div>',		) );		register_sidebar( array(	        'name'          => __( 'Sidebar Footer', 'toroflix' ),	        'id'            => 'sidebar-footer',	        'description'   => __( 'Sidebar Footer del theme', 'toroflix' ),	        'before_widget' => '<div id="%1$s" class="Wdgt %2$s">',	        'after_widget'  => '</div>',	        'before_title'  => '<div class="Title">',	        'after_title'   => '</div>',		) );	}}