<?php add_action( 'widgets_init', function(){    register_widget( 'letters_widget_toroflix' );});class letters_widget_toroflix extends WP_Widget {    #Sets up the widgets name etc    public function __construct() {        $widget_ops = array(            'classname' => 'widget_a-z',            'description' => 'show letters',        );        parent::__construct( 'letters_widget_toroflix', 'Toroflix: Letters', $widget_ops );    }    # Display frontend    public function widget( $argus, $instance ) {        echo $argus['before_widget'];        if ( ! empty( $instance['title'] ) ) {            echo $argus['before_title'] . apply_filters( 'widget_title', $instance['title'] ). $argus['after_title'];        }        get_template_part('public/partials/template/letters');        echo $argus['after_widget'];    }    #Parameters Form of Widget    public function form( $instance ) {        $title = ! empty( $instance['title'] ) ? $instance['title'] : '';        ?>        <div class="wdgt-tt">            <div>                <label for="<?php echo $this->get_field_id( 'title' ); ?>"><?php _e( 'Title:', 'toroflix' ); ?></label>                <div class="fr-input">                    <span class="dashicons dashicons-edit-large"></span>                    <input class="widefat" id="<?php echo $this->get_field_id( 'title' ); ?>" name="<?php echo $this->get_field_name( 'title' ); ?>" type="text" value="<?php echo esc_attr( $title ); ?>">                </div>            </div>        </div>        <?php    }    #Save Data    public function update( $new_instance, $old_instance ) {        // processes widget options to be saved        foreach( $new_instance as $key => $value )        {            $updated_instance[$key] = sanitize_text_field($value);        }        return $updated_instance;    }}