<?php get_header();     if(have_posts()) : while(have_posts()) : the_post();?>        <div class="Body">            <div class="Main Container">                  <article>                    <header class="Top">                        <h1 class="Title"><?php the_title(); ?></h1>                    </header>                    <div class="Description">                        <?php the_content();                         wp_link_pages( array(                            'before' => '<div class="page-links">' . esc_html__( 'Pages:', 'toroflix' ),                            'after'  => '</div>',                        ) ); ?>                    </div>                </article>                                </div>        </div>    <?php endwhile; endif;get_footer(); ?>